public class CustomStack<T>
            {
                private readonly T[] items;
                private int top = -1;
                private readonly int maxSize;
                public CustomStack(int size)
                {
                    items = new T[size];
                    maxSize = size;
                }

                public void Push(T item)
                {
                    if (top == maxSize - 1) throw new ArgumentOutOfRangeException();
                    items[++top] = item;
                }
                public T Pop()
                {
                    if (top == -1) throw new InvalidOperationException("Stack empty");
                    return items[top--];
                }
                public T Peek()
                {
                    if (top == -1) throw new InvalidOperationException("Stack empty");
                    return items[top];
                }

            }

public class CustomQueue<T>
            {
                private readonly T[] items;
                private int front = 0;
                private int rear = -1;
                private int itemCount = 0;
                private readonly int maxSize;
                public CustomQueue(int size)
                {
                    items = new T[size];
                    maxSize = size;
                }
                public T Peek()
                {
                    return items[rear];
                }
                public void Enqueue(T item)
                {
                    if (itemCount == maxSize) throw new ArgumentOutOfRangeException();
                    if (rear == maxSize - 1) rear = -1;
                    items[++rear] = item;
                    ++itemCount;
                }
                public T Dequeue()
                {
                    if (rear == -1) throw new InvalidOperationException("Queue Empty.");
                    if (front == maxSize - 1) front = 0;
                    --itemCount;
                    return items[front++];
                }
          }

public class CustomLinkedList<T>
            {

                private class Node
                {
                    public T Value;
                    public Node NextNode;
                    public Node(T value)
                    {
                        Value = value;
                    }
                }

                private Node First;
                private Node Last;

                public void AddLast(T value)
                {
                    var node = new Node(value);
                    if (First == null)
                    {
                        Last = First = node;
                    }
                    else
                    {
                        Last.NextNode = node;
                        Last = node;
                    }

                }
                public void AddFirst(T value)
                {
                    var node = new Node(value);
                    if (First == null)
                    {
                        Last = First = node;
                    }
                    else
                    {
                        node.NextNode = First;
                        First = node;
                    }

                }

                public int IndexOf(T value)
                {
                    int index = 0;
                    var current = First;
                    while (current.NextNode != null)
                    {
                        if (current.Value.Equals(value))
                            return index;
                        current = current.NextNode;
                        ++index;
                    }
                    return -1;
                }

                public bool Contains(T value)
                {
                    return IndexOf(value) != -1;
                }

                public void RemoveLast()
                {
                    if (First == Last)
                    {
                        First = Last = null;
                        return;
                    }

                    var current = First;
                    while (current.NextNode != null)
                    {
                        if (current.NextNode == Last)
                        {
                            current.NextNode = null;
                            Last = current;
                            break;
                        }
                    }
                }
                public void RemoveFirst()
                {
                    First = First.NextNode;
                }
              }
